// Grab files from the previous node ("Edit Fields1")
const rawFiles = $json.files;

// If it's a stringified JSON array (sometimes happens), parse it
let files;
if (typeof rawFiles === 'string') {
  try {
    files = JSON.parse(rawFiles);
  } catch (e) {
    throw new Error("Failed to parse 'files' string as JSON.");
  }
} else {
  files = rawFiles;
}

// Check if we really have an array now
if (!Array.isArray(files)) {
  throw new Error("'files' is not an array.");
}

// Split into one item per file
return files.map(file => ({
  json: file
}));

const event = $json;

const isThread = !!event.thread_ts;
const session_id = isThread
  ? `${event.user}:${event.thread_ts}`
  : `${event.user}:top`;

const files = event.files || [];

return [{
  json: {
    session_id,
    user: event.user,
    text: event.text || "",
    ts: event.ts,
    thread_ts: event.thread_ts || null,
    is_thread: isThread,
    files // <== array of files stays bundled
  }
}];

