const input = $json.output;

// ‚úÖ Parse flexible duration formats like "3 hours", "1 hour 30 minutes", or "45 minutes"
function parseDuration(durationStr) {
  const hoursMatch = durationStr.match(/(\d+)\s*hour/);
  const minutesMatch = durationStr.match(/(\d+)\s*minute/);

  const hours = hoursMatch ? parseInt(hoursMatch[1], 10) : 0;
  const minutes = minutesMatch ? parseInt(minutesMatch[1], 10) : 0;
  const total = hours * 60 + minutes;

  if (total === 0 || isNaN(total)) {
    throw new Error(`Invalid or missing duration format: "${durationStr}"`);
  }

  return total;
}

const durationMinutes = parseDuration(input.duration);
const windowStart = input.windowStart;
const windowEnd = input.windowEnd;
const preferredDate = input.preferredDate;
const events = input.events || [];
const numDaysToScan = 5;

// ‚úÖ Convert "10:00 AM" into a Date for a given YYYY-MM-DD string
function getTime(dateStr, timeStr) {
  const [time, modifier] = timeStr.trim().split(" ");
  let [hours, minutes] = time.split(":").map(Number);
  if (modifier === "PM" && hours < 12) hours += 12;
  if (modifier === "AM" && hours === 12) hours = 0;

  const date = new Date(`${dateStr}T00:00:00`);
  date.setHours(hours, minutes, 0, 0);
  return date;
}

// ‚úÖ Group "Mandatory" events by day
const busyByDay = {};
for (const ev of events) {
  const start = new Date(ev.start.dateTime);
  const end = new Date(ev.end.dateTime);
  const summary = ev.summary || ev.title || "";
  const isMandatory = summary.toLowerCase().includes("mandatory");

  if (!isMandatory) continue; // ‚úÖ Ignore non-mandatory events

  const day = start.toISOString().split("T")[0];
  if (!busyByDay[day]) busyByDay[day] = [];
  busyByDay[day].push({ start, end });
}

const startScanDate = new Date(preferredDate);

// ‚úÖ Scan for available time slots
for (let i = 0; i < numDaysToScan; i++) {
  const currentDate = new Date(startScanDate);
  currentDate.setDate(currentDate.getDate() + i);
  const dayKey = currentDate.toISOString().split("T")[0];
  const dayStart = getTime(dayKey, windowStart);
  const dayEnd = getTime(dayKey, windowEnd);

  const busy = (busyByDay[dayKey] || [])
    .filter(ev => ev.end > dayStart && ev.start < dayEnd)
    .sort((a, b) => a.start - b.start);

  // ‚úÖ Merge overlapping blocks
  const merged = [];
  for (const block of busy) {
    if (!merged.length || block.start > merged[merged.length - 1].end) {
      merged.push({ ...block });
    } else {
      merged[merged.length - 1].end = new Date(Math.max(merged[merged.length - 1].end, block.end));
    }
  }

  // ‚úÖ Look for available gaps
  let cursor = new Date(dayStart);
  for (const block of merged) {
    const gap = (block.start - cursor) / 60000;
    if (gap >= durationMinutes) {
      const end = new Date(cursor.getTime() + durationMinutes * 60000);
      return [
        {
          json: {
            preferredDate: dayKey,
            startTime: cursor.toLocaleTimeString("en-US", { hour: "numeric", minute: "2-digit" }),
            endTime: end.toLocaleTimeString("en-US", { hour: "numeric", minute: "2-digit" }),
            duration: `${durationMinutes} minutes`
          }
        }
      ];
    }
    cursor = new Date(Math.max(cursor, block.end));
  }

  // ‚úÖ Check after last block
  const remaining = (dayEnd - cursor) / 60000;
  if (remaining >= durationMinutes) {
    const end = new Date(cursor.getTime() + durationMinutes * 60000);
    return [
      {
        json: {
          preferredDate: dayKey,
          startTime: cursor.toLocaleTimeString("en-US", { hour: "numeric", minute: "2-digit" }),
          endTime: end.toLocaleTimeString("en-US", { hour: "numeric", minute: "2-digit" }),
          duration: `${durationMinutes} minutes`
        }
      }
    ];
  }
}

// ‚ùå No time block found
throw new Error("No available time block found within the scanned range.");

